#!/usr/bin/env bash

export LC_ALL=C

set -Eeuo pipefail

setup_vars() {
    # directories and binaries
    WORK_DIR=${WORK_DIR:-"$(pwd)"}
    DEFID_BIN=${DEFID_BIN:-"${WORK_DIR}/defid"}
    DEFI_CLI_BIN=${DEFI_CLI_BIN:-"${WORK_DIR}/defi-cli"}

    # fixture
    OWNERAUTHADDR="mwsZw8nF7pKxWH8eoKL9tPxTpaFkz7QeLU"
    OWNERAUTHPRIV="cRiRQ9cHmy5evDqNDdEV8f6zfbK6epi9Fpz4CRZsmLEmkwy54dWz"
    OPERATORAUTHADDR="mswsMVsyGMj1FzDMbbxw2QW3KvQAv2FKiy"
    OPERATORAUTHPRIV="cPGEaz8AGiM71NGMRybbCqFNRcuUhg3uGvyY4TFE1BZC26EW2PkC"
    ALICE="0x9b8a4af42140d8a4c153a822f02571a1dd037e89"
    # BOB="0x6C34CBb9219d8cAa428835D2073E8ec88BA0a110"
    PRIVKEY_ALICE="af990cc3ba17e776f7f57fcc59942a82846d75833fa17d2ba59ce6858d886e23"
    PRIVKEY_BOB="17b8cb134958b3d8422b6c43b0732fcdb8c713b524df2d45de12f0c7e214ba35"
    CONTRACT_COUNTER=""
    CONTRACT_COUNTERCALLER=""
}

start_node() {
    $DEFID_BIN -regtest \
        -debug=rpc \
        -daemon \
        -printtoconsole \
        -rpcallowip=0.0.0.0/0 \
        -rpcbind=0.0.0.0 \
        -ethrpcbind=0.0.0.0 \
        -masternode_operator="$OPERATORAUTHADDR" \
        -masternode_owner="$OWNERAUTHADDR" \
        -dummypos=0 \
        -subsidytest=1 \
        -txnotokens=0 \
        -txindex=1 \
        -amkheight=0 \
        -bayfrontheight=1 \
        -clarkequayheight=3 \
        -dakotaheight=4 \
        -dakotacrescentheight=5 \
        -eunosheight=6 \
        -eunospayaheight=7 \
        -fortcanningheight=8 \
        -fortcanningmuseumheight=9 \
        -fortcanninghillheight=10 \
        -fortcanningroadheight=11 \
        -fortcanningcrunchheight=12 \
        -fortcanningspringheight=13 \
        -fortcanninggreatworldheight=14 \
        -fortcanningepilogueheight=15 \
        -grandcentralheight=16 \
        -metachainheight=17
}

init_node() {
    sleep 5
}

setup_fixtures() {
    # foundation members
    $DEFI_CLI_BIN -regtest importprivkey "$OWNERAUTHPRIV" owner true
    $DEFI_CLI_BIN -regtest importprivkey "$OPERATORAUTHPRIV" operator true

    # push fixtures
    $DEFI_CLI_BIN -regtest importprivkey "$PRIVKEY_ALICE"
    $DEFI_CLI_BIN -regtest importprivkey "$PRIVKEY_BOB"
    $DEFI_CLI_BIN -regtest generatetoaddress 120 "$OWNERAUTHADDR"

    $DEFI_CLI_BIN -regtest utxostoaccount '{"'"$OWNERAUTHADDR"'":"500@DFI"}'
    $DEFI_CLI_BIN -regtest generatetoaddress 1 "$OWNERAUTHADDR"

    $DEFI_CLI_BIN -regtest setgov '{"ATTRIBUTES": {
                    "v0/params/feature/evm": "true",
                    "v0/params/feature/transferdomain": "true"}}'
    $DEFI_CLI_BIN -regtest generatetoaddress 2 "$OWNERAUTHADDR"

    $DEFI_CLI_BIN -regtest transferdomain '[{"src":{"address":"'"$OWNERAUTHADDR"'", "amount":"200@DFI", "domain":2}, "dst":{"address":"'"$ALICE"'", "amount":"200@DFI", "domain":3}, "singlekeycheck": false}]'
    $DEFI_CLI_BIN -regtest generatetoaddress 1 "$OWNERAUTHADDR"

    $DEFI_CLI_BIN -regtest eth_sendTransaction '{"from":"'"$ALICE"'", "data":"'"$CONTRACT_COUNTER"'", "value":"0x00", "gas":"0x7a120", "gasPrice": "0x22ecb25c00"}'
    $DEFI_CLI_BIN -regtest generatetoaddress 1 "$OWNERAUTHADDR"

    $DEFI_CLI_BIN -regtest eth_sendTransaction '{"from":"'"$ALICE"'", "data":"'"$CONTRACT_COUNTERCALLER"'", "value":"0x00", "gas":"0x7a120", "gasPrice": "0x22ecb25c00"}'
    $DEFI_CLI_BIN -regtest generatetoaddress 1 "$OWNERAUTHADDR"
}

main() {
    setup_vars
    start_node
    init_node
    setup_fixtures
}

main "$@"

